sqlMap.select:
/* domainPrincipal.findByPage */
	SELECT * FROM (
		SELECT a.DOMAIN_ID,a.PRINCIPAL_ID, ROW_NUMBER() OVER(
		ORDER BY $sortColumn$ $sortMethod$
		ORDER BY a.PRINCIPAL_ID ASC
		) as rnum
		FROM DOMAIN_PRINCIPAL a, SECURITY_PRINCIPAL b
		WHERE a.DOMAIN_ID=CAST(#domainId# AS INTEGER) AND a.PRINCIPAL_ID = b.PRINCIPAL_ID
		( b.short_path like '%' || #titleCond# || '%' OR PRINCIPAL_NAME LIKE '%' || #titleCond# ||'%' )	
	) t1 WHERE t1.rnum BETWEEN #startRow# AND #endRow#
