sqlMap.select:
/* userGroup.totalCountGroup */ SELECT COUNT(*) totcnt FROM SECURITY_USER_GROUP WHERE 1=1
GROUP_ID IN ( SELECT PRINCIPAL_ID FROM SECURITY_PRINCIPAL WHERE PRINCIPAL_TYPE='G' AND (DOMAIN_ID=0 OR DOMAIN_ID=#domainId#) )
USER_ID=#userId#

/* userGroup.findGroupByPage */ SELECT * FROM ( SELECT USER_ID,GROUP_ID,ROW_NUMBER() OVER( ORDER BY $sortColumn$ $sortMethod$ ORDER BY SORT_ORDER ASC ) as rnum FROM SECURITY_USER_GROUP WHERE 1=1
GROUP_ID IN ( SELECT PRINCIPAL_ID FROM SECURITY_PRINCIPAL WHERE PRINCIPAL_TYPE='G' AND (DOMAIN_ID=0 OR DOMAIN_ID=#domainId#) )
USER_ID=#userId# ) tb1 WHERE tb1.rnum BETWEEN #startRow# AND #endRow#
