sqlMap.select:
/* calendar.public.list */ SELECT a.CALENDAR_ID, BG_COLOR, OWNER_ID, b.NAME, a.DOMAIN_ID, c.DOMAIN_NM, IS_USE, IS_BILINGUAL, PUBLIC_TYPE FROM CALENDAR_PUBLIC a JOIN CALENDAR_LANG b ON b.CALENDAR_ID = a.CALENDAR_ID JOIN DOMAIN_LANG c ON c.DOMAIN_ID = a.DOMAIN_ID AND c.LANG_KND = b.LANG_KND LEFT OUTER JOIN ( SELECT d.CALENDAR_ID, d.GROUP_ID, e.USER_ID FROM CALENDAR_PUBLIC_GROUP d JOIN USERPASS e ON e.ORG_CD = d.GROUP_ID WHERE e.USER_ID=#admin# ) f ON f.CALENDAR_ID = a.CALENDAR_ID WHERE b.LANG_KND='ko' b.NAME LIKE CONCAT('%',#name#,'%')
a.DOMAIN_ID=#domainId#
ORDER BY a.CALENDAR_ID LIMIT #limit# OFFSET #offset#

/* calendar.user.public.list */ SELECT a.CALENDAR_ID, a.BG_COLOR, OWNER_ID, b.NAME, a.DOMAIN_ID, c.DOMAIN_NM, '1' AS IS_USE, '0' AS IS_BILINGUAL, 'P' AS PUBLIC_TYPE FROM CALENDAR a JOIN CALENDAR_LANG b ON b.CALENDAR_ID = a.CALENDAR_ID JOIN DOMAIN_LANG c ON c.DOMAIN_ID = a.DOMAIN_ID AND c.LANG_KND = b.LANG_KND WHERE a.IS_PUBLIC='Y' AND b.LANG_KND=#langKnd# b.NAME LIKE CONCAT('%',#name#,'%')
a.DOMAIN_ID=#domainId#
ORDER BY a.CALENDAR_ID LIMIT #limit# OFFSET #offset#

sqlMap.insert:
/* calendar.public.insert */
		INSERT INTO CALENDAR_PUBLIC (CALENDAR_ID, DOMAIN_ID, FG_COLOR, BG_COLOR, IS_USE, IS_BILINGUAL, OWNER_ID, PUBLIC_TYPE, INS_DATIM, UPD_DATIM)
		VALUES (CAST(#calendarId# AS DECIMAL), #domainId#, #fgColor#, #bgColor#, #isUse#, #isBilingual#, #ownerId#, #publicType#, NOW(), NOW())

sqlMap.update:
/* calendar.user.public.insert */
		UPDATE CALENDAR SET IS_PUBLIC='Y', UPD_DATIM=NOW()
		WHERE CALENDAR_ID=CAST(#calendarId# AS DECIMAL)

sqlMap.insert:
/* calendar.public.insert.lang */
		INSERT INTO CALENDAR_LANG (CALENDAR_ID, LANG_KND, NAME, COMMENTS, INS_DATIM, UPD_DATIM)
		VALUES (CAST(#calendarId# AS DECIMAL), #langKnd#, #name#, #comments#, NOW(), NOW())

sqlMap.delete:
/* schedule.public.lang.delete */
		DELETE FROM SCHEDULE_LANG
		WHERE SCHEDULE_ID IN (SELECT SCHEDULE_ID FROM SCHEDULE WHERE CALENDAR_ID=CAST(#calendarId# AS DECIMAL))
