sqlMap.select:
/* schedule.user.list */ SELECT a.*
, b.NM_KOR AS USER_NAME , b.NM_ENG AS USER_NAME
FROM ( SELECT SCHEDULE_ID, OWNER_ID AS USER_ID, 'Y' AS IS_OWNER, INS_DATIM, UPD_DATIM FROM SCHEDULE WHERE SCHEDULE_ID=CAST(#scheduleId# AS DECIMAL) AND OWNER_ID=#ownerId# UNION ALL SELECT SCHEDULE_ID, USER_ID, 'N' AS IS_OWNER, INS_DATIM, UPD_DATIM FROM SCHEDULE_USER WHERE SCHEDULE_ID=CAST(#scheduleId# AS DECIMAL) AND USER_ID!=#ownerId# ) a JOIN USERPASS b ON b.USER_ID = a.USER_ID ORDER BY IS_OWNER DESC

sqlMap.insert:
/* schedule.user.insert */
		INSERT INTO SCHEDULE_USER (SCHEDULE_ID, USER_ID, INS_DATIM, UPD_DATIM)
		VALUES (CAST(#scheduleId# AS DECIMAL), #userId#, NOW(), NOW())

sqlMap.delete:
/* schedule.user.delete.all */
		DELETE FROM SCHEDULE_USER
		WHERE SCHEDULE_ID=CAST(#scheduleId# AS DECIMAL)

/* schedule.user.delete.all.calendar */
		DELETE FROM SCHEDULE_USER
		WHERE SCHEDULE_ID IN (SELECT SCHEDULE_ID FROM SCHEDULE WHERE CALENDAR_ID=CAST(#calendarId# AS DECIMAL))
