sqlMap.update:
/* group.changeParent */
UPDATE SECURITY_PRINCIPAL SET PARENT_ID=CAST(#parentId# AS INTEGER), PRINCIPAL_ORDER=(SELECT MAX(PRINCIPAL_ORDER)+1 FROM SECURITY_PRINCIPAL WHERE PARENT_ID=CAST(#parentId# AS INTEGER))
WHERE PRINCIPAL_ID=CAST(#id# AS INTEGER)

sqlMap.delete:
/* group.deleteGrade */
DELETE FROM SECURITY_GRADE_GROUP 
WHERE GROUP_ID=CAST(#groupId# AS INTEGER) AND GRADE_ID=CAST(#gradeId# AS INTEGER)

/* group.deleteAllGrade */
DELETE FROM SECURITY_GRADE_GROUP 
WHERE GROUP_ID=CAST(#groupId# AS INTEGER)
