sqlMap.select:
/* userPreference.find */ SELECT USER_ID, DOMAIN_ID, SYSTEM_CODE, NAME, VALUE FROM USER_PREFERENCE WHERE USER_ID=#userId# AND DOMAIN_ID=CAST(#domainId# AS INTEGER)
AND SYSTEM_CODE=#systemCode#
AND NAME=#name#

sqlMap.update:
/* userPreference.deleteAll */ DELETE FROM USER_PREFERENCE WHERE USER_ID=#userId# AND DOMAIN_ID=CAST(#domainId# AS INTEGER)
AND SYSTEM_CODE=#systemCode#
AND NAME=#name#
