sqlMap.delete:
/* schedule.delete */
		DELETE FROM SCHEDULE 
		WHERE SCHEDULE_ID=CAST(#scheduleId# AS INTEGER)

/* schedule.delete.owner */
		DELETE FROM SCHEDULE 
		WHERE SCHEDULE_ID=CAST(#scheduleId# AS INTEGER) AND OWNER_ID=#ownerId#

/* schedule.delete.joint */
		DELETE FROM SCHEDULE 
		WHERE SCHEDULE_ID=CAST(#scheduleId# AS INTEGER) AND OWNER_ID=#ownerId#

/* schedule.delete.calendar */
		DELETE FROM SCHEDULE 
		WHERE CALENDAR_ID=CAST(#calendarId# AS INTEGER)

sqlMap.select:
/* schedule.list.ical.user */ SELECT ALLDAY, START_DATIM, END_DATIM, NAME, COMMENTS, PLACE, FREQ, UNTIL, INTERVAL, BYMONTHDAY, BYDAY, a.INS_DATIM, a.UPD_DATIM FROM SCHEDULE a JOIN SCHEDULE_LANG B ON B.SCHEDULE_ID = A.SCHEDULE_ID AND LANG_KND=#langKnd# JOIN CALENDAR C ON C.CALENDAR_ID = A.CALENDAR_ID WHERE a.CALENDAR_ID=CAST(#calendarId# AS INTEGER)
( ( #start# <= TO_CHAR(START_DATIM, 'YYYY') AND TO_CHAR(START_DATIM, 'YYYY') < #end# ) OR ( START_DATIM <= TO_DATE(#start#||'01', 'YYYYMM') AND FREQ IS NOT NULL AND UNTIL IS NULL) OR TO_DATE(#start#||'01', 'YYYYMM') <= TO_DATE(UNTIL, 'YYYY') )
ORDER BY START_DATIM

/* schedule.list.ical.public */ SELECT ALLDAY, START_DATIM, END_DATIM, NAME, COMMENTS, PLACE, FREQ, UNTIL, INTERVAL, BYMONTHDAY, BYDAY, a.INS_DATIM, a.UPD_DATIM FROM SCHEDULE a JOIN SCHEDULE_LANG B ON B.SCHEDULE_ID = A.SCHEDULE_ID AND LANG_KND=#langKnd# JOIN CALENDAR_PUBLIC C ON C.CALENDAR_ID = A.CALENDAR_ID WHERE a.CALENDAR_ID=CAST(#calendarId# AS INTEGER)
( ( #start# <= TO_CHAR(START_DATIM, 'YYYY') AND TO_CHAR(START_DATIM, 'YYYY') < #end# ) OR ( START_DATIM <= TO_DATE(#start#||'01', 'YYYYMM') AND FREQ IS NOT NULL AND UNTIL IS NULL) OR TO_DATE(#start#||'01', 'YYYYMM') <= TO_DATE(UNTIL, 'YYYYMMDD') )
ORDER BY START_DATIM
