/* eventUser.getCriteriaEventList */ SELECT B.* FROM ( SELECT A.*,ROWNUM AS RNUM FROM ( SELECT T1.EVENT_SEQ ,T1.DOMAIN_ID ,T1.CATEGORY_SEQ
,T1.TITLE_KOR AS TITLE ,T1.CONTENTS_KOR AS CONTENTS ,T1.IMAGE_PATH_ENG AS IMAGE_PATH_SUB ,T1.PLACE_BUILDING_KOR || T1.PLACE_NM AS PLACE_BUILDING ,T1.IMAGE_PATH_KOR AS IMAGE_PATH ,T1.IMAGE_NM_KOR AS IMAGE_NM ,T3.NM_KOR AS CATEGORY_NM
,T1.TITLE_ENG AS TITLE ,T1.CONTENTS_ENG AS CONTENTS ,T1.IMAGE_PATH_KOR AS IMAGE_PATH_SUB ,T1.PLACE_BUILDING_ENG || T1.PLACE_NM AS PLACE_BUILDING ,T1.IMAGE_PATH_ENG AS IMAGE_PATH ,T1.IMAGE_NM_ENG AS IMAGE_NM ,T3.NM_ENG AS CATEGORY_NM
,TO_CHAR(T1.START_DATE,'YYYY/MM/DD') AS START_DATE ,TO_CHAR(T1.END_DATE,'YYYY/MM/DD') AS END_DATE ,SUBSTR(T1.START_TIME,1,2)||':'||SUBSTR(T1.START_TIME,3,4) AS START_TIME ,SUBSTR(T1.END_TIME,1,2)||':'||SUBSTR(T1.END_TIME,3,4) AS END_TIME ,TO_CHAR(T1.EVENT_START,'YYYY/MM/DD') AS EVENT_START_DATE ,TO_CHAR(T1.EVENT_END,'YYYY/MM/DD') AS EVENT_END_DATE ,SUBSTR(T1.EVENT_START_TIME,1,2)||':'||SUBSTR(T1.EVENT_START_TIME,3,4) AS EVENT_START_TIME ,SUBSTR(T1.EVENT_END_TIME,1,2)||':'||SUBSTR(T1.EVENT_END_TIME,3,4) AS EVENT_END_TIME ,T1.TARGET_LINK ,T1.REG_DATIM FROM EVENT T1 ,EVENT_TARGET T2 ,EVENT_CATEGORY T3 WHERE T1.EVENT_SEQ = T2.EVENT_SEQ AND T1.CATEGORY_SEQ = T3.CATEGORY_SEQ AND T1.STATE ='1' AND T1.DOMAIN_ID = #DOMAIN_ID# AND T2.DEPT_CODE = #GROUP_ID# /*오늘기준*/ AND TO_CHAR(T1.START_DATE,'YYYYMMDD') <= #SYS_DATE# /*시작된이벤트*/ AND TO_CHAR(T1.END_DATE,'YYYYMMDD') >= #SYS_DATE# /*종료않된이벤트*/
/*특정일지정*/ AND TO_CHAR(T1.START_DATE,'YYYYMMDD') <= #SRCH_END_DATE# /*Interval(주/월)*/ AND TO_CHAR(T1.END_DATE,'YYYYMMDD') >= #SRCH_STD_DATE#
/*이벤트명*/
AND T1.TITLE_KOR LIKE '%'||#SRCH_STR#||'%'
AND T1.TITLE_ENG LIKE '%'||#SRCH_STR#||'%'
/*카테고리*/ AND T1.CATEGORY_SEQ = #SRCH_CATE#
/*등록기관 - 등록자하부조직명*/
AND T1.DEPT_CODE_KOR = #SRCH_DEPT#
AND T1.DEPT_CODE_ENG = #SRCH_DEPT#
ORDER BY REG_DATIM DESC ) A ) B WHERE RNUM BETWEEN ((#PAGE_NUM#-1)*#PAGE_SIZE#+1) AND #PAGE_NUM#*#PAGE_SIZE#

/* eventUser.getCriteriaEventListTotCnt */ SELECT COUNT(*) AS CNT FROM EVENT T1 ,EVENT_TARGET T2 ,EVENT_CATEGORY T3 WHERE T1.EVENT_SEQ = T2.EVENT_SEQ AND T1.CATEGORY_SEQ = T3.CATEGORY_SEQ AND T1.STATE ='1' AND T1.DOMAIN_ID = CAST(#DOMAIN_ID# AS INTEGER) AND T2.DEPT_CODE = #GROUP_ID# /*오늘기준*/ AND TO_CHAR(T1.START_DATE,'YYYYMMDD') <= #SYS_DATE# /*시작된이벤트*/ AND TO_CHAR(T1.END_DATE,'YYYYMMDD') >= #SYS_DATE# /*종료않된이벤트*/
/*특정일지정*/ AND TO_CHAR(T1.START_DATE,'YYYYMMDD') <= #SRCH_END_DATE# /*Interval(주/월)*/ AND TO_CHAR(T1.END_DATE,'YYYYMMDD') >= #SRCH_STD_DATE#
/*이벤트명*/
AND T1.TITLE_KOR LIKE '%'||#SRCH_STR#||'%'
AND T1.TITLE_ENG LIKE '%'||#SRCH_STR#||'%'
/*카테고리*/ AND T1.CATEGORY_SEQ = CAST(#SRCH_CATE# AS INTEGER)
/*등록기관 - 등록자하부조직명*/
AND T1.DEPT_CODE_KOR = #SRCH_DEPT#
AND T1.DEPT_CODE_ENG = #SRCH_DEPT#
