sqlMap.insert:
/* calendar.public.group.insert */
		INSERT INTO CALENDAR_PUBLIC_GROUP (CALENDAR_ID, GROUP_ID, PERMISSIONS, INS_DATIM, UPD_DATIM)
		VALUES (CAST(#calendarId# AS INTEGER), #groupId#, #permissions#, GETDATE(), GETDATE())

sqlMap.update:
/* calendar.public.group.update */
		UPDATE CALENDAR_PUBLIC_GROUP SET PERMISSIONS=#permissions#, UPD_DATIM=GETDATE()
		WHERE CALENDAR_ID=CAST(#calendarId# AS INTEGER) AND GROUP_ID=#groupId#

sqlMap.delete:
/* calendar.public.group.delete.all */
		DELETE FROM CALENDAR_PUBLIC_GROUP
		WHERE CALENDAR_ID=CAST(#calendarId# AS INTEGER)
